import React, { useState, useEffect } from 'react';
import { io, Socket } from 'socket.io-client';
import './App.css';
import HomePage from './pages/HomePage';
import GameRoom from './pages/GameRoom';
import { Player, GameState, ChatMessage } from './types/game';

const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || "http://localhost:3000";


// Main App Component
function App() {
  const [socket, setSocket] = useState<Socket | null>(null);
  const [gameState, setGameState] = useState<GameState | null>(null);
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    const newSocket = io(SOCKET_URL, {
      transports: ["websocket", "polling"], // Render works best with both
      path: "/socket.io",
    });
    setSocket(newSocket);

    newSocket.on('connect', () => {
      setIsConnected(true);
    });

    newSocket.on('disconnect', () => {
      setIsConnected(false);
    });

    newSocket.on('game_created', (data: { gameId: string; player: Player }) => {
      setCurrentPlayer(data.player);
    });

    newSocket.on('game_joined', (data: { gameId: string; player: Player }) => {
      setCurrentPlayer(data.player);
    });

    newSocket.on('game_state', (state: GameState) => {
      setGameState(state);
    });

    newSocket.on('chat_message', (message: ChatMessage) => {
      // Update game state with new chat message
      setGameState(prevState => {
        if (!prevState) return prevState;
        return {
          ...prevState,
          chatMessages: [...prevState.chatMessages, message]
        };
      });
    });

    newSocket.on('house_completed', (data: { house: any; winner: any }) => {
      console.log('House completed:', data);
      // You can add visual feedback here if needed
    });


    return () => {
      newSocket.close();
    };
  }, []);

  const handleLeaveGame = () => {
    if (socket && gameState) {
      socket.emit('leave_game', { gameId: gameState.id });
    }
    setGameState(null);
    setCurrentPlayer(null);
  };

  if (!isConnected) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Connecting to server...</p>
      </div>
    );
  }

  if (gameState && currentPlayer) {
    return (
      <GameRoom
        socket={socket}
        gameState={gameState}
        currentPlayer={currentPlayer}
        onLeaveGame={handleLeaveGame}
      />
    );
  }

  return (
    <HomePage
      socket={socket}
    />
  );
}

export default App;
